PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "artisans"
(
    id                        INTEGER       not null
        primary key autoincrement,
    maker_id                  VARCHAR(16)   not null,
    name                      VARCHAR(128)  not null,
    formerly                  VARCHAR(256)  not null,
    intro                     VARCHAR(512)  not null,
    since                     VARCHAR(16)   not null,
    country                   VARCHAR(16)   not null,
    state                     VARCHAR(32)   not null,
    city                      VARCHAR(32)   not null,
    production_models         VARCHAR(256)  not null,
    styles                    VARCHAR(1024) not null,
    order_types               VARCHAR(1024) not null,
    features                  VARCHAR(1024) not null,
    other_features            VARCHAR(1024) not null,
    payment_plans             VARCHAR(256)  not null,
    species_does              VARCHAR(256)  not null,
    species_doesnt            VARCHAR(256)  not null,
    languages                 VARCHAR(256)  not null,
    notes                     CLOB          not null,
    contact_allowed           VARCHAR(16),
    contact_method            VARCHAR(32)   not null,
    contact_info_obfuscated   VARCHAR(128)  not null,
    inactive_reason           VARCHAR(512)  not null,
    other_styles              VARCHAR(1024) not null,
    other_order_types         VARCHAR(1024) not null,
    production_models_comment VARCHAR(256)  not null,
    styles_comment            VARCHAR(256)  not null,
    order_types_comment       VARCHAR(256)  not null,
    features_comment          VARCHAR(256)  not null,
    payment_methods           VARCHAR(256)  not null,
    currencies_accepted       VARCHAR(64)   not null,
    species_comment           VARCHAR(256)  not null
);
COMMIT;
